html,
body {
  margin: 0;
  box-sizing: border-box;
  font-size: 62.5%;
  font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, Oxygen, Ubuntu, Cantarell,
    'Open Sans', 'Helvetica Neue', sans-serif;
}

*,
*::before,
*::after {
  padding: 0;
  margin: 0;
  box-sizing: inherit;
}

#app {
  display: grid;
  grid-template:
    'nav nav nav' 50px
    'sidebar editor view' 120px 1fr 1fr;
  height: 100vh;

  &-nav-bar {
    width: 100vw;
    grid-area: nav;
  }

  &-side-bar {
    grid-area: sidebar;
  }
  &-editor {
    grid-area: editor;
  }
  &-view {
    grid-area: view;
  }
}
